import React, { Component } from 'react'

export default class counter extends Component {
    state = {
        count: 0
    }
    increment = () => {
        // 获取对应ref的value
        const { value } = this.sel
        const { count } = this.state
        this.setState({
            // 强制类型转换
            count: value * 1 + count
        })
    }
    decrement = () => {
        // 获取对应ref的value
        const { value } = this.sel
        const { count } = this.state
        this.setState({
            // 强制类型转换
            count: count - value * 1
        })
    }
    incrementifodd = () => {
        // 获取对应ref的value
        const { value } = this.sel
        const { count } = this.state
        if (count % 2 !== 0) {
            this.setState({
                // 强制类型转换
                count: value * 1 + count
            })
        }

    }
    incrementyb = () => {
        setTimeout(() => {
            // 获取对应ref的value
            const { value } = this.sel
            const { count } = this.state
            this.setState({
                // 强制类型转换
                count: value * 1 + count
            })
        }, 500);
    }
    render() {
        return (
            <div style={{ marginTop: "10%", marginLeft: "35%" }}>
                <h2>
                    所得到的结果是{this.state.count}
                </h2>
                <select name="" id="" ref={c => this.sel = c}>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                </select>&nbsp;
                <button onClick={this.increment}>相加</button> &nbsp;
                <button onClick={this.decrement}>相减</button> &nbsp;
                <button onClick={this.incrementifodd}>结果为奇数时相加</button> &nbsp;
                <button onClick={this.incrementyb}>异步相加</button> &nbsp;
            </div>
        )
    }
}
