import React, { Component } from 'react'
import store from '../redux/store'

export default class counter extends Component {
    state = {
        // count: 0
    }
    increment = () => {
        // 获取对应ref的value
        const { value } = this.sel
        store.dispath({
            type:"increment",
            data:value*1
        })
    }
    decrement = () => {
        // 获取对应ref的value
        const { value } = this.sel
        store.dispath({
            type:"decrement",
            data:value*1
        })
    }
    incrementifodd = () => {
        // 获取对应ref的value
        const { value } = this.sel
        if (count % 2 !== 0) {
            store.dispath({
                type:"decrement",
                data:value*1
            })
        }

    }
    incrementyb = () => {
        setTimeout(() => {
            // 获取对应ref的value
            const { value } = this.sel
            store.dispath({
                type:"decrement",
                data:value*1
            })
        }, 500);
    }
    render() {
        return (
            <div style={{ marginTop: "10%", marginLeft: "35%" }}>
                <h2>
                    所得到的结果是{store.setState()}
                </h2>
                <select name="" id="" ref={c => this.sel = c}>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                </select>&nbsp;
                <button onClick={this.increment}>相加</button> &nbsp;
                <button onClick={this.decrement}>相减</button> &nbsp;
                <button onClick={this.incrementifodd}>结果为奇数时相加</button> &nbsp;
                <button onClick={this.incrementyb}>异步相加</button> &nbsp;
            </div>
        )
    }
}
